import { useEffect } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertSubscriptionSchema, type InsertSubscription, type Subscription } from "@shared/schema";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Button } from "@/components/ui/button";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface EditSubscriptionDialogProps {
  subscription: Subscription;
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export default function EditSubscriptionDialog({ subscription, open, onOpenChange }: EditSubscriptionDialogProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<InsertSubscription>({
    resolver: zodResolver(insertSubscriptionSchema),
    defaultValues: {
      name: "",
      category: "",
      cost: "",
      billingCycle: "monthly",
      renewalDate: "",
      status: "active",
      logoUrl: "",
      description: ""
    }
  });

  useEffect(() => {
    if (subscription && open) {
      form.reset({
        name: subscription.name,
        category: subscription.category,
        cost: subscription.cost,
        billingCycle: subscription.billingCycle,
        renewalDate: new Date(subscription.renewalDate).toISOString().split('T')[0],
        status: subscription.status,
        logoUrl: subscription.logoUrl || "",
        description: subscription.description || ""
      });
    }
  }, [subscription, open, form]);

  const updateMutation = useMutation({
    mutationFn: async (data: InsertSubscription) => {
      const response = await apiRequest("PATCH", `/api/subscriptions/${subscription.id}`, data);
      return response.json();
    }
  });

  useEffect(() => {
    if (updateMutation.isSuccess) {
      queryClient.invalidateQueries({ queryKey: ["/api/subscriptions"] });
      toast({ title: "Subscription updated successfully" });
      onOpenChange(false);
    }
  }, [updateMutation.isSuccess, queryClient, toast, onOpenChange]);

  useEffect(() => {
    if (updateMutation.isError) {
      toast({ title: "Failed to update subscription", variant: "destructive" });
    }
  }, [updateMutation.isError, toast]);

  const onSubmit = (data: InsertSubscription) => {
    updateMutation.mutate(data);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-md" data-testid="dialog-edit-subscription">
        <DialogHeader>
          <DialogTitle>Edit Subscription</DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Name</FormLabel>
                  <FormControl>
                    <Input placeholder="Netflix, Spotify, etc." {...field} data-testid="input-edit-name" />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="category"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Category</FormLabel>
                  <Select onValueChange={field.onChange} value={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-edit-category">
                        <SelectValue placeholder="Select category" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="Streaming">Streaming</SelectItem>
                      <SelectItem value="Software">Software</SelectItem>
                      <SelectItem value="Fitness">Fitness</SelectItem>
                      <SelectItem value="News & Media">News & Media</SelectItem>
                      <SelectItem value="Cloud Storage">Cloud Storage</SelectItem>
                      <SelectItem value="Productivity">Productivity</SelectItem>
                      <SelectItem value="Music">Music</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="cost"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Cost</FormLabel>
                    <FormControl>
                      <Input type="number" step="0.01" placeholder="9.99" {...field} data-testid="input-edit-cost" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="billingCycle"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Billing Cycle</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-edit-billing-cycle">
                          <SelectValue />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="monthly">Monthly</SelectItem>
                        <SelectItem value="annual">Annual</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="renewalDate"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Next Renewal Date</FormLabel>
                  <FormControl>
                    <Input type="date" {...field} data-testid="input-edit-renewal-date" />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="status"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Status</FormLabel>
                  <Select onValueChange={field.onChange} value={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-edit-status">
                        <SelectValue />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="active">Active</SelectItem>
                      <SelectItem value="inactive">Inactive</SelectItem>
                      <SelectItem value="expiring">Expiring Soon</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="logoUrl"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Logo URL (Optional)</FormLabel>
                  <FormControl>
                    <Input placeholder="https://example.com/logo.png" {...field} value={field.value || ""} data-testid="input-edit-logo-url" />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex justify-end space-x-2 pt-4">
              <Button 
                type="button" 
                variant="outline" 
                onClick={() => onOpenChange(false)}
                data-testid="button-edit-cancel"
              >
                Cancel
              </Button>
              <Button 
                type="submit" 
                disabled={updateMutation.isPending}
                data-testid="button-edit-save"
              >
                {updateMutation.isPending ? "Updating..." : "Update Subscription"}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/filter-sidebar.tsx.content:
import { Card, CardContent } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { Search } from "lucide-react";

interface Filters {
  search: string;
  category: string;
  costRange: string[];
  status: string[];
  renewalDate: string;
}

interface FilterSidebarProps {
  filters: Filters;
  onFiltersChange: (filters: Filters) => void;
}

export default function FilterSidebar({ filters, onFiltersChange }: FilterSidebarProps) {
  const updateFilter = (key: keyof Filters, value: any) => {
    onFiltersChange({ ...filters, [key]: value });
  };

  const toggleArrayFilter = (key: 'costRange' | 'status', value: string) => {
    const currentArray = filters[key];
    const newArray = currentArray.includes(value)
      ? currentArray.filter(item => item !== value)
      : [...currentArray, value];
    updateFilter(key, newArray);
  };

  return (
    <Card className="bg-card rounded-lg border border-border shadow-sm sticky top-6" data-testid="filter-sidebar">
      <CardContent className="p-6">
        <h3 className="text-lg font-semibold text-foreground mb-4">Filters</h3>

        {/* Search */}
        <div className="mb-6">
          <Label className="block text-sm font-medium text-foreground mb-2">Search</Label>
          <div className="relative">
            <Input
              type="text"
              placeholder="Search subscriptions..."
              value={filters.search}
              onChange={(e) => updateFilter('search', e.target.value)}
              className="pl-10"
              data-testid="input-search"
            />
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
          </div>
        </div>

        {/* Category Filter */}
        <div className="mb-6">
          <Label className="block text-sm font-medium text-foreground mb-2">Category</Label>
          <Select value={filters.category || "all"} onValueChange={(value) => updateFilter('category', value === 'all' ? '' : value)}>
            <SelectTrigger data-testid="select-category">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Categories</SelectItem>
              <SelectItem value="Streaming">Streaming</SelectItem>
              <SelectItem value="Software">Software</SelectItem>
              <SelectItem value="Fitness">Fitness</SelectItem>
              <SelectItem value="News & Media">News & Media</SelectItem>
              <SelectItem value="Cloud Storage">Cloud Storage</SelectItem>
              <SelectItem value="Productivity">Productivity</SelectItem>
              <SelectItem value="Music">Music</SelectItem>
            </SelectContent>
          </Select>
        </div>

        {/* Cost Range */}
        <div className="mb-6">
          <Label className="block text-sm font-medium text-foreground mb-2">Monthly Cost Range</Label>
          <div className="space-y-2">
            {[
              { value: "under-10", label: "Under $10" },
              { value: "10-25", label: "$10 - $25" },
              { value: "25-50", label: "$25 - $50" },
              { value: "over-50", label: "Over $50" }
            ].map((range) => (
              <div key={range.value} className="flex items-center space-x-2">
                <Checkbox
                  id={range.value}
                  checked={filters.costRange.includes(range.value)}
                  onCheckedChange={() => toggleArrayFilter('costRange', range.value)}
                  data-testid={`checkbox-cost-${range.value}`}
                />
                <Label htmlFor={range.value} className="text-sm text-foreground cursor-pointer">
                  {range.label}
                </Label>
              </div>
            ))}
          </div>
        </div>

        {/* Status Filter */}
        <div className="mb-6">
          <Label className="block text-sm font-medium text-foreground mb-2">Status</Label>
          <div className="space-y-2">
            {[
              { value: "active", label: "Active" },
              { value: "inactive", label: "Inactive" },
              { value: "expiring", label: "Expiring Soon" }
            ].map((status) => (
              <div key={status.value} className="flex items-center space-x-2">
                <Checkbox
                  id={status.value}
                  checked={filters.status.includes(status.value)}
                  onCheckedChange={() => toggleArrayFilter('status', status.value)}
                  data-testid={`checkbox-status-${status.value}`}
                />
                <Label htmlFor={status.value} className="text-sm text-foreground cursor-pointer">
                  {status.label}
                </Label>
              </div>
            ))}
          </div>
        </div>

        {/* Renewal Date */}
        <div>
          <Label className="block text-sm font-medium text-foreground mb-2">Renewal Date</Label>
          <Select value={filters.renewalDate || "all"} onValueChange={(value) => updateFilter('renewalDate', value === 'all' ? '' : value)}>
            <SelectTrigger data-testid="select-renewal-date">
              <SelectValue />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="all">All Dates</SelectItem>
              <SelectItem value="next-7">Next 7 days</SelectItem>
              <SelectItem value="next-30">Next 30 days</SelectItem>
              <SelectItem value="next-90">Next 3 months</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/metrics-cards.tsx.content:
import { type Subscription } from "@shared/schema";
import { Card, CardContent } from "@/components/ui/card";
import { Calendar, TrendingUp, Star, AlertTriangle } from "lucide-react";

interface MetricsCardsProps {
  subscriptions: Subscription[];
}

export default function MetricsCards({ subscriptions }: MetricsCardsProps) {
  const metrics = {
    totalMonthly: subscriptions
      .filter(sub => sub.status === "active")
      .reduce((sum, sub) => {
        if (sub.billingCycle === "monthly") {
          return sum + parseFloat(sub.cost);
        } else if (sub.billingCycle === "annual") {
          return sum + (parseFloat(sub.cost) / 12);
        }
        return sum;
      }, 0),

    totalAnnual: subscriptions
      .filter(sub => sub.status === "active")
      .reduce((sum, sub) => {
        if (sub.billingCycle === "annual") {
          return sum + parseFloat(sub.cost);
        } else if (sub.billingCycle === "monthly") {
          return sum + (parseFloat(sub.cost) * 12);
        }
        return sum;
      }, 0),

    activeCount: subscriptions.filter(sub => sub.status === "active").length,

    expiringSoon: subscriptions.filter(sub => {
      if (sub.status !== "active") return false;
      const now = new Date();
      const renewalDate = new Date(sub.renewalDate);
      const daysDiff = Math.ceil((renewalDate.getTime() - now.getTime()) / (1000 * 60 * 60 * 24));
      return daysDiff <= 30 && daysDiff >= 0;
    }).length
  };

  return (
    <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
      <Card className="bg-card rounded-lg border border-border shadow-sm" data-testid="card-total-monthly">
        <CardContent className="p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-muted-foreground text-sm font-medium">Total Monthly</p>
              <p className="text-3xl font-bold text-foreground" data-testid="text-total-monthly">
                ${metrics.totalMonthly.toFixed(2)}
              </p>
            </div>
            <div className="w-12 h-12 bg-primary/10 rounded-lg flex items-center justify-center">
              <Calendar className="text-primary text-xl" />
            </div>
          </div>
        </CardContent>
      </Card>

      <Card className="bg-card rounded-lg border border-border shadow-sm" data-testid="card-total-annual">
        <CardContent className="p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-muted-foreground text-sm font-medium">Total Annual</p>
              <p className="text-3xl font-bold text-foreground" data-testid="text-total-annual">
                ${metrics.totalAnnual.toFixed(2)}
              </p>
            </div>
            <div className="w-12 h-12 bg-success/10 rounded-lg flex items-center justify-center">
              <TrendingUp className="text-success text-xl" />
            </div>
          </div>
        </CardContent>
      </Card>

      <Card className="bg-card rounded-lg border border-border shadow-sm" data-testid="card-active-count">
        <CardContent className="p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-muted-foreground text-sm font-medium">Active Subscriptions</p>
              <p className="text-3xl font-bold text-foreground" data-testid="text-active-count">
                {metrics.activeCount}
              </p>
            </div>
            <div className="w-12 h-12 bg-warning/10 rounded-lg flex items-center justify-center">
              <Star className="text-warning text-xl" />
            </div>
          </div>
        </CardContent>
      </Card>

      <Card className="bg-card rounded-lg border border-border shadow-sm" data-testid="card-expiring-soon">
        <CardContent className="p-6">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-muted-foreground text-sm font-medium">Expiring Soon</p>
              <p className="text-3xl font-bold text-destructive" data-testid="text-expiring-soon">
                {metrics.expiringSoon}
              </p>
            </div>
            <div className="w-12 h-12 bg-destructive/10 rounded-lg flex items-center justify-center">
              <AlertTriangle className="text-destructive text-xl" />
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/protected-route.ts.content:
import { ReactNode } from 'react';
import { useAuth } from '@/contexts/auth-context';
import LoginPage from '@/pages/login';

interface ProtectedRouteProps {
  children: ReactNode;
}

export function ProtectedRoute({ children }: ProtectedRouteProps) {
  const { user, isAuthenticated, isLoading } = useAuth();

  // Show loading state while auth is being determined
  if (isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 dark:from-gray-900 dark:to-gray-800 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-300">Checking authentication...</p>
        </div>
      </div>
    );
  }

  // Redirect to login if not authenticated
  if (!isAuthenticated || !user) {
    return <LoginPage />;
  }

  // Render protected content
  return <>{children}</>;
}

client/src/components/service-card.tsx.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { type AvailableService } from "@shared/schema";
import { Card, CardContent, CardDescription, CardFooter, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { Star, Check, Zap } from "lucide-react";

interface ServiceCardProps {
  service: AvailableService;
  "data-testid"?: string;
}

export default function ServiceCard({ service, "data-testid": testId }: ServiceCardProps) {
  const [selectedPlan, setSelectedPlan] = useState<string>("");
  const [showSubscribeDialog, setShowSubscribeDialog] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const subscriptionMutation = useMutation({
    mutationFn: async (data: { serviceId: string; planId: string }) => {
      return await apiRequest("POST", "/api/subscriptions/subscribe", data);
    },
    onSuccess: () => {
      // Invalidate both subscriptions and expiring subscriptions caches
      queryClient.invalidateQueries({ queryKey: ["/api/subscriptions"] });
      queryClient.invalidateQueries({ queryKey: ["/api/subscriptions/expiring?days=30"] });
      toast({
        title: "Subscribed Successfully!",
        description: `You've been subscribed to ${service.name}.`,
      });
      setShowSubscribeDialog(false);
      setSelectedPlan("");
    },
    onError: (error: any) => {
      let title = "Subscription Failed";
      let description = "Failed to subscribe to the service. Please try again.";

      if (error.message && error.message.includes("401")) {
        title = "Authentication Required";
        description = "Please log in to subscribe to services. Refresh the page and sign in.";
      } else if (error.message) {
        description = error.message;
      }

      toast({
        title,
        description,
        variant: "destructive",
      });
    },
  });

  const handleSubscribe = () => {
    if (!selectedPlan) {
      toast({
        title: "Please select a plan",
        description: "Choose a subscription plan to continue.",
        variant: "destructive",
      });
      return;
    }

    subscriptionMutation.mutate({
      serviceId: service.id,
      planId: selectedPlan,
    });
  };

  const selectedPlanDetails = service.plans.find((plan: any) => plan.id === selectedPlan);

  return (
    <Card className="group hover:shadow-lg transition-all duration-200 border-2 hover:border-blue-200 dark:hover:border-blue-800" data-testid={testId}>
      <CardHeader className="pb-3">
        <div className="flex items-start justify-between">
          <div className="flex items-center gap-3">
            {service.logoUrl && (
              <img
                src={service.logoUrl}
                alt={`${service.name} logo`}
                className="w-12 h-12 object-contain rounded-lg"
                data-testid={`img-service-logo-${service.id}`}
              />
            )}
            <div>
              <CardTitle className="text-xl font-bold" data-testid={`text-service-name-${service.id}`}>
                {service.name}
              </CardTitle>
              <div className="flex items-center gap-2 mt-1">
                <Badge variant="secondary" data-testid={`badge-category-${service.id}`}>
                  {service.category}
                </Badge>
                {service.isPopular && (
                  <Badge className="bg-yellow-100 text-yellow-800 hover:bg-yellow-200 dark:bg-yellow-900 dark:text-yellow-200" data-testid={`badge-popular-${service.id}`}>
                    <Star className="w-3 h-3 mr-1" />
                    Popular
                  </Badge>
                )}
              </div>
            </div>
          </div>
        </div>
        <CardDescription className="mt-3" data-testid={`text-service-description-${service.id}`}>
          {service.description}
        </CardDescription>
      </CardHeader>

      <CardContent className="pt-0">
        {/* Key Features */}
        {service.features && service.features.length > 0 && (
          <div className="mb-4">
            <h4 className="font-semibold text-sm text-gray-700 dark:text-gray-300 mb-2" data-testid={`text-features-title-${service.id}`}>
              Key Features
            </h4>
            <div className="space-y-1">
              {service.features.slice(0, 3).map((feature: string, index: number) => (
                <div key={index} className="flex items-center gap-2 text-sm text-gray-600 dark:text-gray-400" data-testid={`text-feature-${service.id}-${index}`}>
                  <Check className="w-4 h-4 text-green-500 shrink-0" />
                  {feature}
                </div>
              ))}
            </div>
          </div>
        )}

        {/* Pricing Plans */}
        <div className="space-y-2">
          <h4 className="font-semibold text-sm text-gray-700 dark:text-gray-300" data-testid={`text-plans-title-${service.id}`}>
            Pricing Plans
          </h4>
          {service.plans.map((plan: any, index: number) => (
            <div 
              key={plan.id} 
              className="flex items-center justify-between p-3 rounded-lg border bg-gray-50 dark:bg-gray-800 dark:border-gray-700"
              data-testid={`div-plan-${service.id}-${plan.id}`}
            >
              <div>
                <div className="font-medium" data-testid={`text-plan-name-${service.id}-${plan.id}`}>
                  {plan.name}
                </div>
                <div className="text-sm text-gray-600 dark:text-gray-400" data-testid={`text-plan-billing-${service.id}-${plan.id}`}>
                  {plan.billingCycle}
                </div>
              </div>
              <div className="text-right">
                <div className="font-bold text-lg" data-testid={`text-plan-price-${service.id}-${plan.id}`}>
                  ${plan.price}
                </div>
                <div className="text-sm text-gray-500" data-testid={`text-plan-period-${service.id}-${plan.id}`}>
                  per {plan.billingCycle === 'annual' ? 'year' : 'month'}
                </div>
              </div>
            </div>
          ))}
        </div>
      </CardContent>

      <CardFooter>
        <Dialog open={showSubscribeDialog} onOpenChange={setShowSubscribeDialog}>
          <DialogTrigger asChild>
            <Button 
              className="w-full group-hover:bg-blue-600 group-hover:text-white transition-colors" 
              size="lg"
              data-testid={`button-subscribe-${service.id}`}
            >
              <Zap className="w-4 h-4 mr-2" />
              Subscribe Now
            </Button>
          </DialogTrigger>
          <DialogContent className="sm:max-w-md" data-testid={`dialog-subscribe-${service.id}`}>
            <DialogHeader>
              <DialogTitle className="flex items-center gap-3" data-testid={`text-dialog-title-${service.id}`}>
                {service.logoUrl && (
                  <img
                    src={service.logoUrl}
                    alt={`${service.name} logo`}
                    className="w-8 h-8 object-contain rounded"
                  />
                )}
                Subscribe to {service.name}
              </DialogTitle>
              <DialogDescription data-testid={`text-dialog-description-${service.id}`}>
                Choose your subscription plan to get started instantly.
              </DialogDescription>
            </DialogHeader>

            <div className="space-y-4 py-4">
              <div>
                <label className="text-sm font-medium mb-2 block" data-testid={`text-plan-selector-label-${service.id}`}>
                  Select Plan
                </label>
                <Select value={selectedPlan} onValueChange={setSelectedPlan}>
                  <SelectTrigger data-testid={`select-plan-${service.id}`}>
                    <SelectValue placeholder="Choose a plan" />
                  </SelectTrigger>
                  <SelectContent>
                    {service.plans.map((plan: any) => (
                      <SelectItem key={plan.id} value={plan.id} data-testid={`option-plan-${service.id}-${plan.id}`}>
                        <div className="flex items-center justify-between w-full">
                          <span>{plan.name}</span>
                          <span className="ml-4 font-medium">${plan.price}/{plan.billingCycle === 'annual' ? 'year' : 'month'}</span>
                        </div>
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              {selectedPlanDetails && (
                <div className="p-4 bg-blue-50 dark:bg-blue-950 rounded-lg" data-testid={`div-plan-details-${service.id}`}>
                  <h4 className="font-semibold text-blue-900 dark:text-blue-100 mb-2">
                    {selectedPlanDetails.name} - ${selectedPlanDetails.price}/{selectedPlanDetails.billingCycle === 'annual' ? 'year' : 'month'}
                  </h4>
                  <p className="text-sm text-blue-800 dark:text-blue-200">
                    Your subscription will automatically renew every {selectedPlanDetails.billingCycle === 'annual' ? 'year' : 'month'}.
                  </p>
                </div>
              )}

              <div className="flex gap-3">
                <Button
                  variant="outline"
                  onClick={() => setShowSubscribeDialog(false)}
                  className="flex-1"
                  data-testid={`button-cancel-${service.id}`}
                >
                  Cancel
                </Button>
                <Button
                  onClick={handleSubscribe}
                  disabled={!selectedPlan || subscriptionMutation.isPending}
                  className="flex-1"
                  data-testid={`button-confirm-subscribe-${service.id}`}
                >
                  {subscriptionMutation.isPending ? "Subscribing..." : "Subscribe"}
                </Button>
              </div>
            </div>
          </DialogContent>
        </Dialog>
      </CardFooter>
    </Card>
  );
}

client/src/components/subscription-card..content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { type Subscription } from "@shared/schema";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { BarChart3, Edit, X, RotateCcw, Trash2, ExternalLink, Play } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import EditSubscriptionDialog from "./edit-subscription-dialog";
import { launchService } from "@/utils/launch-helpers";

interface SubscriptionCardProps {
  subscription: Subscription;
}

export default function SubscriptionCard({ subscription }: SubscriptionCardProps) {
  const [showEditDialog, setShowEditDialog] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isLaunching, setIsLaunching] = useState(false);

  const updateStatusMutation = useMutation({
    mutationFn: async (newStatus: string) => {
      await apiRequest("PATCH", `/api/subscriptions/${subscription.id}`, { status: newStatus });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/subscriptions"] });
      toast({ title: "Subscription updated successfully" });
    },
    onError: () => {
      toast({ title: "Failed to update subscription", variant: "destructive" });
    }
  });

  const deleteMutation = useMutation({
    mutationFn: async () => {
      await apiRequest("DELETE", `/api/subscriptions/${subscription.id}`);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/subscriptions"] });
      toast({ title: "Subscription deleted successfully" });
    },
    onError: () => {
      toast({ title: "Failed to delete subscription", variant: "destructive" });
    }
  });

  const getStatusBadge = (status: string) => {
    const styles = {
      active: "status-active",
      inactive: "status-inactive", 
      expiring: "status-expiring"
    };

    const labels = {
      active: "Active",
      inactive: "Inactive",
      expiring: "Expiring Soon"
    };

    return (
      <Badge className={`text-xs px-2 py-1 font-medium ${styles[status as keyof typeof styles] || styles.inactive}`}>
        {labels[status as keyof typeof labels] || status}
      </Badge>
    );
  };

  const handleCancel = () => {
    updateStatusMutation.mutate("inactive");
  };

  const handleReactivate = () => {
    updateStatusMutation.mutate("active");
  };

  const handleDelete = () => {
    if (window.confirm("Are you sure you want to delete this subscription?")) {
      deleteMutation.mutate();
    }
  };

  const handleLaunch = async () => {
    setIsLaunching(true);
    try {
      const success = await launchService(subscription.id, subscription.name);
      if (success) {
        toast({
          title: "Service Launched",
          description: `Opened ${subscription.name} in a new tab`,
        });
        // Refresh subscriptions to update lastUsed timestamp
        queryClient.invalidateQueries({ queryKey: ["/api/subscriptions"] });
      } else {
        toast({
          title: "Launch Failed", 
          description: "Unable to launch this service",
          variant: "destructive",
        });
      }
    } catch (error) {
      toast({
        title: "Launch Error",
        description: "Failed to launch service. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsLaunching(false);
    }
  };

  return (
    <>
      <Card className="bg-card rounded-lg border border-border shadow-sm hover:shadow-md transition-shadow" data-testid={`card-subscription-${subscription.id}`}>
        <CardContent className="p-6">
          <div className="flex items-center justify-between mb-4">
            {subscription.logoUrl && (
              <img 
